newtype Transformer f g = Transform { ($$) :: forall i. f i ~> g i }
type family (~>) :: k -> k -> * where (~>) = (->) (~>) = Transformer
type a < -> b = (a -> b, b -> a) type a < ~> b = (a ~> b, b ~> a)

newtype Ran0 p (f :: k -> *) a = Ran0 { ran0 :: forall r. r => (a ~> p r) -> f r }
newtype Ran1 p (f :: k -> j -> *) a b = Ran1 { ran1 :: forall r. r => (a ~> p r) -> f r b }
-- ...
data RanOp0 p (f :: k -> *) a = forall e. e => RanOp0 (a ~> p e) (f e)
-- ...
data Lan0 p (f :: k -> *) a = forall e. e => Lan0 (p e ~> a) (f e)
data Lan1 p (f :: k -> j -> *) a b = forall e. e => Lan1 (p e ~> a) (f e b)
-- ...
data LanOp0 p (f :: k -> *) a = LanOp0 { lan0 :: forall r. r => (p r -> a) -> f r }
-- ...

class Covariant (f :: i -> j) where comap :: (a ~> b) -> (f a ~> f b)
class Contravariant f where contramap :: (b ~> a) -> (f a ~> f b)
class Covariant m => Monad (m :: i -> i) where pure :: a ~> m a join :: m (m a) ~> m a
class Covariant w => Comonad (w :: i -> i) where extract :: w a ~> a split :: w a ~> w (w a)
class Couniversal f | f -> where couniversal :: r => (a ~> r) -> (f a ~> r)
class Universal f | f -> where universal :: e => (e ~> a) -> (e ~> f a)
instance Covariant (Free0 ) where comap f (Free0 e) = Free0 (e . (.f))
instance Monad (Free0 ) where pure x = Free0 $ \k -> k x join (Free0 e) = Free0 $ \k -> e $ \(Free0 e) -> e k
instance Couniversal (Free0 ) where couniversal h (Free0 e) = e h
-- ...

